// src/pages/LoginPage.js
import React, { useState } from 'react';
import { Form, Button, Container } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import RegistrationModal from '../components/RegistrationModal';
import '../styles/LoginPage.css'; // Import the custom CSS

const LoginPage = () => {
  const navigate = useNavigate();

  const [show, setShow] = useState(false);

  const handleRegisterClick = () => {
    setShow(true);
    console.log('Modal should open', show);  // This will still show the previous state due to batching
  };

  const handleClose = () => {
    setShow(false);
  };

  return (
    <Container className="login-container d-flex flex-column align-items-center justify-content-center">
      <h2 className="login-title">Login</h2>
      <Form className="login-form">
        <Form.Group controlId="formEmail">
          <Form.Label>Email address</Form.Label>
          <Form.Control type="email" placeholder="Enter email" />
        </Form.Group>
        <Form.Group controlId="formPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control type="password" placeholder="Password" />
        </Form.Group>
        <Button variant="primary" type="submit" className="my-3">
          Register
        </Button>
        <div className="text-center my-3">OR</div>
        <Button variant="secondary" className="mb-3" onClick={handleRegisterClick}>
          Create a new account
        </Button>
        <RegistrationModal show={show} onHide={handleClose} />
        <a href="#" className="d-block text-center">Forgot your password?</a>
      </Form>
    </Container>
  );
};

export default LoginPage;
