// src/pages/LoginPage.js
import React, { useState } from 'react';
import { Form, Button, Container} from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import RegistrationModal from '../components/RegistrationModal';

const LoginPage = () => {
  const navigate = useNavigate();

  const [show, setShow] = useState(false);

  const handleRegisterClick = () => {
  setShow(true);
  console.log('Modal should open', show);  // This will still show the previous state due to batching
};

  const handleClose = () => {
    setShow(false);
  };

  return (
    <Container className="d-flex flex-column align-items-center my-5">
      <h2>Login</h2>
      <Form className="w-50">
        <Form.Group controlId="formEmail">
          <Form.Label>Email address</Form.Label>
          <Form.Control type="email" placeholder="Enter email" />
        </Form.Group>
        <Form.Group controlId="formPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control type="password" placeholder="Password" />
        </Form.Group>
        <Button variant="primary" type="submit" className="my-3">
          Register
        </Button>
        <div className="text-center my-3">OR</div>
        <Button variant="secondary" className="mb-3" onClick={handleRegisterClick}>
          Create a new account
        </Button>
            {/* <RegistrationModal show={show} onHide={handleClose} /> */}

        <Modal show={show} onHide={handleClose} animation={false}>
          <Modal.Header closeButton>
            <Modal.Title>Modal heading</Modal.Title>
          </Modal.Header>
          <Modal.Body>Woohoo, you are reading this text in a modal!</Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
              Close
            </Button>
            <Button variant="primary" onClick={handleClose}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
        <a href="#" className="d-block text-center">Forgot your password?</a>
      </Form>
    </Container>
  );
};

export default LoginPage;
