import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import '../styles/Settings.css';
// import 'bootstrap/dist/css/bootstrap.min.css';
// import { Container, Row, Col, Button } from 'react-bootstrap';

const Settings = () => {
  const [isSubscriptionOpen, setIsSubscriptionOpen] = useState(false);
  const [isAppearanceOpen, setIsAppearanceOpen] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    if (location.state?.openSubscription) {
      setIsSubscriptionOpen(true);
    }
  }, [location.state]);

  const goToPayment = () => {
      navigate('./payment');
  };

  const toggleSubscription = () => {
    setIsSubscriptionOpen(!isSubscriptionOpen);
    setIsAppearanceOpen(false); // Close Appearances if it's open
  };

  const toggleAppearance = () => {
    setIsAppearanceOpen(!isAppearanceOpen);
    setIsSubscriptionOpen(false); // Close Subscription Plan if it's open
  };

  return (
    <div className="settings">
      <div className="page-wide-element">
        <div className="title-wrapper" onClick={toggleSubscription}>
          <span className="settings-header">Subscription Plan</span>
          <button onClick={goToPayment} className="edit-payment-button">
            Edit Payment Method
          </button>
        </div>
        {isSubscriptionOpen && (
          <div className="content">
          {/* Display subscription plans as cards horizontally */}
          <div className="subscription-cards">
            <div className="card">Plan 1</div>
            <div className="card">Plan 2</div>
            <div className="card">Plan 3</div>
            <div className="card">Plan 4</div>
            <div className="card">Plan 5</div>
          </div>
        </div>
        )}
      </div>

      <div className="page-wide-element">
        <div className="title-wrapper" onClick={toggleAppearance}>
          <h2>Appearances</h2>
        </div>
        {isAppearanceOpen && (
          <div className="content">
            Appearances content
          </div>
        )}
      </div>
    </div>
  );
};

export default Settings;
