// pages/Settings.js
import React, { useState } from 'react';

const Settings = () => {
  const [isSubscriptionOpen, setIsSubscriptionOpen] = useState(false);
  const [isAppearanceOpen, setIsAppearanceOpen] = useState(false);

  const toggleSubscription = () => {
    setIsSubscriptionOpen(!isSubscriptionOpen);
    setIsAppearanceOpen(false); // Close Appearances if it's open
  };

  const toggleAppearance = () => {
    setIsAppearanceOpen(!isAppearanceOpen);
    setIsSubscriptionOpen(false); // Close Subscription Plan if it's open
  };

  return (
    <div className="settings">
      <div className="page-wide-element" onClick={toggleSubscription}>
        <h2>Subscription Plan</h2>
        {isSubscriptionOpen && (
          <div className="content">
            {/* Subscription Plan content */}
            {/* Add your subscription plan content here */}
          </div>
        )}
      </div>

      <div className="page-wide-element" onClick={toggleAppearance}>
        <h2>Appearances</h2>
        {isAppearanceOpen && (
          <div className="content">
            {/* Appearances content */}
            {/* Add your appearances content here */}
          </div>
        )}
      </div>
    </div>
  );
};

export default Settings;
