// pages/Settings.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Payment = () => {
  const [isPayPalOpen, setIsPayPalOpen] = useState(false);
  const [isCardOpen, setIsCardOpen] = useState(false);
  const [isInvoiceOpen, setIsInvoiceOpen] = useState(false);
  const navigate = useNavigate();

  const goToSettings = () => {
      navigate('../');
  };

  const togglePayPal = () => {
    setIsPayPalOpen(!isPayPalOpen);
    setIsCardOpen(false); 
    setIsInvoiceOpen(false); 
  };

  const toggleCard = () => {
    setIsCardOpen(!isCardOpen);
    setIsPayPalOpen(false); 
    setIsInvoiceOpen(false); 
  };

  const toggleInvoice = () => {
    setIsInvoiceOpen(!isInvoiceOpen);
    setIsPayPalOpen(false); 
    setIsCardOpen(false); 
  };

  return (
    <div className="payment">
      <div className="page-wide-element" onClick={togglePayPal}>
        <h2>PayPal</h2>
        {isPayPalOpen && (
          <div className="content">
            paypal
          </div>
        )}
      </div>

      <div className="page-wide-element" onClick={toggleCard}>
        <h2>Credit Card</h2>
        {isCardOpen && (
          <div className="content">
            Card
          </div>
        )}
      </div>
      <div className="page-wide-element" onClick={toggleInvoice}>
        <h2>Invoice</h2>
        {isInvoiceOpen && (
          <div className="content">
            Invoice
          </div>
        )}
      </div>
      <button onClick={goToSettings}/>
    </div>

  );
};

export default Payment;
